  `aName` varchar(50) NOT NULL COMMENT '姓名',
  `aPetName` varchar(50) default NULL COMMENT '昵称',
  `aHead` varchar(225) default NULL COMMENT '头像',
  `aPwd` varchar(11) NOT NULL COMMENT '密码',
  `aEmail` varchar(50) NOT NULL COMMENT '邮箱',
  `aSex` varchar(4) default NULL COMMENT '性别',
  `aPhone` varchar(11) default NULL COMMENT '电话',
