  PRIMARY KEY  (`cId`),
  KEY `aId` (`aId`),
  CONSTRAINT `t_comment_ibfk_1` FOREIGN KEY (`aId`) REFERENCES `t_author` (`aId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
