  PRIMARY KEY  (`ckId`),
  KEY `author_id` (`aId`),
  CONSTRAINT `t_cookbook_ibfk_1` FOREIGN KEY (`aId`) REFERENCES `t_author` (`aId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

